import React from 'react';
import axios from 'axios';

export class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users: null,
            mail: [],
            pseudo: [],
            role: []

        };
        this.componentWillMount = this.componentWillMount.bind(this);
    }

    componentWillMount() {
        if (this.state.users === null) {
            axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                .then(response => {
                    this.setState({users: response.data});
                    for (let i = 0; i < this.state.users.length; i++) {
                        let newStateMail = this.state.mail.slice();
                        newStateMail.push(this.state.users[i]['Mail']);
                        this.setState({mail: newStateMail});


                        let newStatePseudo = this.state.pseudo.slice();
                        newStatePseudo.push(this.state.users[i]['Pseudo']);
                        this.setState({pseudo: newStatePseudo});

                        let newStateRole = this.state.role.slice();
                        newStateRole.push(this.state.users[i]['Role']);
                        this.setState({role: newStateRole});
                    }
                })
                .catch(error => console.log(error))
        }
    };


    chargeData(){

    }


    render() {


        console.log(this.state.users);
        return (
            <div className="Users">
                <div className="header">
                    <p>Username</p>
                    <p>Mail</p>
                    <p>Role</p>
                </div>

            </div>
        );
    }
}

export default Users;
