import React, {useEffect} from "react";
import $ from 'jquery';
import {FormControl, InputLabel, MenuItem, Select} from "@material-ui/core";

function Modal(props) {

    function customFunction(){
        props.mainAction(props.mainActionParameters)
        closeModal();
    }

    function changeValue(e){
        props.changeAction(e.target.value)
    }

    function closeModal(){
        $('.Modal').css("display", "none");
        props.close();
    }

    function openModal(){
        $('.Modal').css("display", "block");
    }

    useEffect(() => {
        if(props.open){
            openModal();
        }
    }, [props.open]);


    return (
        <div className="Modal">
            <div>
                <h3>{props.title}</h3>
                <p>{props.message}</p>
                <FormControl>
                    <InputLabel>Role</InputLabel>
                    <Select onChange={changeValue} defaultValue="">
                        <MenuItem value={"administrator"}>Administrator</MenuItem>
                        <MenuItem value={"user"}>User</MenuItem>
                        <MenuItem value={"super-admin"}>Super-administrator</MenuItem>
                    </Select>
                </FormControl>
                <div className="action">
                    <button className="button filled" onClick={customFunction}>{props.actionButton}</button>
                    <button className="button outlined" onClick={closeModal}>{props.closeButton}</button>
                </div>
            </div>
        </div>
    );
}

export default Modal;
