import React, {useEffect, useState} from "react";
import $ from 'jquery';

import {faUser, faEnvelope, faLock} from '@fortawesome/free-solid-svg-icons'
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

function ModalSignUp(props) {
    const [description, setDescription] = useState(props.oldDescription);

    function customFunction() {
        props.mainAction();
        closeModal();
    }


    function changeValue(e) {
        setDescription(e.target.value);
        props.changeAction(e.target.value);

    }


    function closeModal() {
        $('.ModalSignUp').css("display", "none");
        props.close();
    }

    function openModal() {
        $('.ModalSignUp').css("display", "block");
    }

    useEffect(() => {
        if (props.open) {
            openModal();
        }
    }, [props.open]);

    return (
        <div className="ModalSignUp">
            <div>
                <h3>{props.title}</h3>
                <p>{props.message}</p>
                <div>
                    <FontAwesomeIcon icon={faUser}/>
                    <input type="text" id="username" name="username" required placeholder={"Username"}/>
                </div>
                <div>
                    <FontAwesomeIcon icon={faEnvelope}/>
                    <input type="mail" id="mail" name="mail" required placeholder={"Mail"}/>
                </div>
                <div>
                    <FontAwesomeIcon icon={faLock}/>
                    <input type="password" id="password" name="password" required placeholder={"Password"}/>
                </div>
                <div>

                    <FontAwesomeIcon icon={faLock}/>
                    <input type="password" id="confirmPassword" name="confirmPassword" required placeholder="&#xf0e0"/>
                </div>



                <div className="action">
                    <button className="button outlined" onClick={closeModal}>{props.closeButton}</button>
                    <button className="button filled" onClick={customFunction}>{props.actionButton}</button>
                </div>
            </div>
        </div>
    );
}

export default ModalSignUp;
