import React, {useEffect} from "react";
import $ from 'jquery';

function ModalEditHome(props) {

    function customFunction() {
        props.mainAction();
        closeModal();
    }


    function changeValue(e) {
        props.changeAction(e.target.value);
    }


    function closeModal() {
        $('.ModalEditHome').css("display", "none");
        props.close();
    }

    function openModal() {
        $('.ModalEditHome').css("display", "block");

    }

    useEffect(() => {
        if (props.open) {
            openModal();
        }
    }, [props.open]);

    return (
        <div className="ModalEditHome">
            <div>
                <h3>{props.title}</h3>
                <p>{props.message}</p>
                <div className="container">
                    <div className="text">
                        <textarea className="textinput" rows="15" cols="132" name="description"
                                  value={props.oldDescription} onChange={changeValue}>{props.oldDescription}</textarea>
                    </div>
                </div>

                <div className="action">
                    <button className="button outlined" onClick={closeModal}>{props.closeButton}</button>
                    <button className="button filled" onClick={customFunction}>{props.actionButton}</button>
                </div>
            </div>
        </div>
    );
}

export default ModalEditHome;
