import React, {useState} from 'react';
import axios from 'axios';
import Issues from "../components/quiz/issues";
import Historic from "../components/quiz/historic";

export class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users : null,
            currentIssue: null,
            currentDecisions: [],
            historic: [],
            step: 0,
        };
        // this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidMount(){
        if(!this.state.users){
            axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                .then(response => {
                    this.setState({tree: response.data});
                    this.changeData(response.data.entree[0].ID_Critere);
                })
                .catch(error => console.log(error))
        }
    };

    render(){
        return (
            <div className="Users">
                <h2>USERS</h2>
            </div>
        );
    }
}

export default Users;
