import React from 'react'
import axios from 'axios';
import pdf from '../public/documentsRessources/Compte-Rendu-de-Réunion-7.pdf';
import {Document} from "react-pdf";
class FileUpload extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            file: null,
            showFile: 'Compte-Rendu-de-Réunion-7.pdf'
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.onChange = this.onChange.bind(this)
        this.uploadFile = this.uploadFile.bind(this)
    }

    async onSubmit(e) {
        e.preventDefault()
        let res = await this.uploadFile(this.state.file);
        console.log(res.data);
    }

    onChange(e) {
        this.setState({file: e.target.files[0]})
    }

    async uploadFile(file) {
        const formData = new FormData();
        formData.append('file', file)
        return await axios.post('http://localhost/Projet_TER/API/Controllers/uploadFile.php', formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        });
    }


    componentDidMount() {
        if (this.state.description === null) {
            axios.get('http://localhost/Projet_TER/API/Controllers/readFile.php')
                .then(response => {
                    this.setState({description: response.data['description']});
                })
                .catch(error => console.log(error))
        }
    };

    render() {
        console.log(pdf);
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <h1> React File Upload Example</h1>
                    <input type="file" onChange={this.onChange}/>
                    <button type="submit">Upload File</button>
                    <div className="headerProfile-menu-list"
                         onClick={() => window.open("%PUBLIC_URL%/".concat(this.state.showFile))}> PDF
                    </div>

                </form>
                <Document
    file="../public/documentsRessources/Compte-Rendu-de-Réunion-7.pdf"
    />
            </div>
        )
    }
}

export default FileUpload;
