import React from 'react';
import axios from 'axios';

export class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users: null,
            mail: [],
            pseudo: [],
            role: []

        };
        // this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidMount() {
        if (this.state.users === null)
            axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                .then(response => {
                    this.setState({users: response.data});
                    for (let i = 0; i < this.state.users['utilisateur'].length; i++) {
                        let newStateMail = this.state.mail.slice();
                        newStateMail.push(this.state.users['utilisateur'][i]['Mail']);
                        this.setState({mail: newStateMail});


                        let newStatePseudo = this.state.pseudo.slice();
                        newStatePseudo.push(this.state.users['utilisateur'][i]['Pseudo']);
                        this.setState({pseudo: newStatePseudo});

                        let newStateRole = this.state.role.slice();
                        newStateRole.push(this.state.users['utilisateur'][i]['Role']);
                        this.setState({role: newStateRole});
                    }
                })
                .catch(error => console.log(error))
    };

    render() {

        return (<table>
            <thead>
            <tr className="row100 head">
                <th className="cell100 column1">Employees</th>
            </tr>
            </thead>
            <tbody>
            <tr className="row100 body">
                <td className="cell100 column1">Brandon Green</td>
            </tr>
            <tr className="row100 body">
                <td className="cell100 column1">Kathy Daniels</td>
            </tr>
            <tr className="row100 body">
                <td className="cell100 column1">Elizabeth Alvarado</td>
            </tr>
            <tr className="row100 body">
                <td className="cell100 column1">Michael Coleman</td>
            </tr>
            <tr className="row100 body">
                <td className="cell100 column1">Jason Cox</td>
            </tr>
            <tr className="row100 body">
                <td className="cell100 column1">Christian Perkins</td>
            </tr>
            <tr className="row100 body">
                <td className="cell100 column1">Emily Wheeler</td>
            </tr>
            </tbody>
        </table>);
        /*return (
            <div className="Users">
                <p>
                <table>
                    <tr>
                        <p>Username</p>
                    </tr>

                </table>
                </p>
                <p>{this.state.mail[0]}</p>
                <p>{this.state.pseudo}</p>
                <p>{this.state.role}</p>

            </div>
        );*/
    }
}

export default Users;
