import React from 'react';
import logo from '../logothedre.png';
import axios from "axios";
export default  class Home extends React.Component { // Tell webpack this JS file uses this image


    constructor(props) {
        super(props);
        this.state = {
            datas : null
        }
    }

    componentDidMount() {
        if (this.state.datas === null) {
            axios.get('http://localhost/Projet_TER/API/Controllers/accueil/lire.php')
                .then(response => {
                    this.setState({datas: response.data});
                })
                .catch(error => console.log(error))
        }
    };

    render(){

        if (this.state.datas === null) return (<p>Loading...</p>);
        else return (
            <div >

                <div className="logo">
                    <img src={logo} width="500" height="500"  alt={'LogoThedre'}/>
                </div>
                <div className="accueil">
                    <p>{this.state.datas}</p>
                </div>
            </div>
        );
    }
}
