import React from 'react'
import axios from 'axios';

class FileUpload extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            file: null,
            datas: null,
            id_methode: null,
            staticLink: 'http://localhost/reactTest/MATUI/src/public/documentsRessources/ExamenIHM_CorentinRoy.pdf'
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.onChange = this.onChange.bind(this)
        this.uploadFile = this.uploadFile.bind(this)
    }

    async onSubmit(e) {
        e.preventDefault()
        let res = await this.uploadFile(this.state.file);
        console.log(res.data);
    }

    onChange(e) {
        this.setState({file: e.target.files[0]})
    }

    handleChange = (event) => {
        console.log(this.state.id_methode);
        this.setState({id_methode: {"id_methode": event.target.value}});
        this.show();
    };

    async uploadFile(file) {
        const formData = new FormData();
        formData.append('file', file)
        return await axios.post('http://localhost/reactTest/MATUI/API/Controllers/uploadFile.php', formData, {
            headers: {
                'content-type': 'multipart/form-data'
            }
        });
    }

    open = () => {
        let protocol = window.location.protocol;
        let host = window.location.hostname;
        let url = protocol + '//' + host;
        let dynamicPdf = 'ExamenIHM_CorentinRoy.pdf';
        window.open(url + '/Projet_TER/src/public/documentsRessources/' + dynamicPdf);
    }

    show() {
        let protocol = window.location.protocol;
        let host = window.location.hostname;
        let url = protocol + '//' + host;
        if (this.state.datas === null && this.state.id_methode !== null) {
            const json = JSON.stringify(this.state.id_methode);
            axios.post(url + '/Projet_TER/API/Controllers/ressource/lireRessourcesMethode.php', json)
                .then(response => {
                    this.setState({datas: response.data['Ressources']});
                })
                .catch(error => console.log(error))
        }
    }

    componentDidMount() {
        // let protocol = window.location.protocol;
        // let host = window.location.hostname;
        // let url = protocol + '//' + host;
        // if (this.state.datas === null) {
        //     const json = JSON.stringify(this.state.id_methode);
        //     axios.post(url + '/Projet_TER/API/Controllers/ressource/lireRessourcesMethode.php', json)
        //         .then(response => {
        //             this.setState({datas: response.data});
        //         })
        //         .catch(error => console.log(error))
        // }
    };

    render() {
        if (this.state.datas !== null) console.log(this.state.datas);
        else return (<form>
            <input onChange={this.handleChange.bind(this)} type="number" id="name" name="name"/>


        </form>)
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <h1> React File Upload Example</h1>
                    <input type="file" onChange={this.onChange}/>
                    <button type="submit">Upload File</button>
                </form>

                <form onChange={this.handleChange.bind(this)} onSubmit={this.show.bind(this)}>
                    <input type="number" id="name" name="name"/>

                </form>

                <br/>
                <a href={this.state.staticLink}>open pdf (statick link)</a>
                <br/>
                <p onClick={this.open}>open pdf (function dynamic link)</p>
                <p>  {this.state.datas.map(data => <p key={data}>{data.Nom}</p>)}</p>
            </div>
        )
    }
}

export default FileUpload;
