import React from 'react';
import axios from 'axios';

export class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users: null,
            mail: [],
            pseudo: [],
            role: []

        };
        // this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidMount() {
        if (this.state.users === null)
            axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                .then(response => {
                    this.setState({users: response.data});
                    for (let i = 0; i < this.state.users['utilisateur'].length; i++) {
                        let newStateMail = this.state.mail.slice();
                        newStateMail.push(this.state.users['utilisateur'][i]['Mail']);
                        this.setState({mail: newStateMail});


                        let newStatePseudo = this.state.pseudo.slice();
                        newStatePseudo.push(this.state.users['utilisateur'][i]['Pseudo']);
                        this.setState({pseudo: newStatePseudo});

                        let newStateRole = this.state.role.slice();
                        newStateRole.push(this.state.users['utilisateur'][i]['Role']);
                        this.setState({role: newStateRole});
                    }
                })
                .catch(error => console.log(error))
    };

    render() {

        return (
            <div className="Users">
                <div className="container">
                    <div className="table">
                        <div className="table-header">
                            <div className="header__item">Pseudo</div>
                            <div className="header__item">Mail</div>
                            <div className="header__item">Role</div>
                        </div>
                        <div className="table-content">
                            <div className="table-row">
                                <div className="table-data">Tom</div>
                                <div className="table-data">2</div>
                                <div className="table-data">0</div>
                            </div>
                            <div className="table-row">
                                <div className="table-data">Tom</div>
                                <div className="table-data">2</div>
                                <div className="table-data">0</div>
                            </div>
                    </div>
                </div>
            </div>
            </div>);

        /*return (
            <div className="Users">
                <p>
                <table>
                    <tr>
                        <p>Username</p>
                    </tr>

                </table>
                </p>
                <p>{this.state.mail[0]}</p>
                <p>{this.state.pseudo}</p>
                <p>{this.state.role}</p>

            </div>
        );*/
    }
}

export default Users;
