import React from 'react';
import logo from '../public/logothedre.png';
import axios from "axios";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPen} from "@fortawesome/free-solid-svg-icons";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogActions from "@material-ui/core/DialogActions";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import {TextField} from "@material-ui/core";

export default class Home extends React.Component { // Tell webpack this JS file uses this image


    constructor(props) {
        super(props);
        this.state = {
            description: null,
            modalOpen: false,
            newDescription: null,

        }
        this.componentDidMount = this.componentDidMount.bind(this);
        this.edit = this.edit.bind(this);

        this.handleClose = this.handleClose.bind(this);
        this.handleClickOpen = this.handleClickOpen.bind(this);

    }

    componentDidMount() {
        if (this.state.description === null) {
            axios.get('http://localhost/Projet_TER/API/Controllers/accueil/lire.php')
                .then(response => {
                    this.setState({description: response.data['description']});
                })
                .catch(error => console.log(error))
        }
    };

    handleClickOpen() {
        this.setState({modalOpen: true});
    };

    handleClose = () => {
        this.setState({modalOpen: false});
    };

    edit(role) {
        if (this.state.idUser !== null && role !== null) {
            let data = JSON.stringify({id_utilisateur: this.state.idUser, role: role});
            console.log(data);
            axios({
                method: 'put',
                url: 'http://localhost/Projet_TER/API/Controllers/utilisateur/modifierUtilisateur.php',
                data: data
            })
                .then(response => {
                    axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                        .then(response => {
                            this.setState({users: response.data});
                        })
                        .catch(error => console.log(error))
                    this.handleClose();
                })
                .catch(function (erreur) {
                    console.log(erreur);
                });

        }
        this.handleClose();
    }


    handleChange = (event) => {
        this.setState({newDescription: event.target.value});
    };


    render() {
        if (this.state.description === null) return (<p>Loading...</p>);
        else return (
            <div className="Home">
                <div className="Logo">
                    <img src={logo} alt={'Logo Thedre'}/>
                </div>
                <div className="Accueil">
                    <h3> Contents <FontAwesomeIcon icon={faPen}  onClick={this.handleClickOpen}/></h3>
                    <Dialog
                        open={this.state.modalOpen}
                        onClose={() => this.handleClose}
                    >
                        <DialogTitle>Modify home content</DialogTitle>
                        <DialogContent>
                            <TextField
                                id="outlined-multiline-static"
                                label="Description"
                                multiline
                                rows={10}
                                defaultValue={this.state.description}
                                variant="outlined"
                                onChange={this.handleChange.bind(this)}
                            />
                        </DialogContent>

                        <DialogActions>
                            <Button onClick={this.handleClose}>
                                Cancel
                            </Button>
                            <Button
                                onClick={this.handleClose}>
                                Ok
                            </Button>
                        </DialogActions>

                    </Dialog>
                    <p>{this.state.description}</p>
                </div>
            </div>
        );
    }
}
