import React from 'react';
import axios from 'axios';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faEnvelope, faPen, faTrashAlt, faUser, faUserTag} from '@fortawesome/free-solid-svg-icons'
import {Modal} from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import TextField from "@material-ui/core/TextField";
import DialogActions from "@material-ui/core/DialogActions";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";

export class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users: null,
            modalOpen : false,
            idUser : null,
            roleUser : null,
        };
        this.componentDidMount = this.componentDidMount.bind(this);
        this.edit = this.edit.bind(this);
        this.delete = this.delete.bind(this);
    }

    handleClickOpen(id, role) {
        this.setState({modalOpen : true, idUser : id, roleUser: role});
    };

    handleClose = () => {
        this.setState({modalOpen : false});
    };

    componentDidMount() {
        if (this.state.users === null) {
            axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                .then(response => {
                    this.setState({users: response.data});
                })
                .catch(error => console.log(error))
        }
    };


    edit(id, role) {
        if (id !== null || role !==null) {
            let data = JSON.stringify({id: Number(id)});
            console.log(data);
            axios({
                method: 'delete',
                url: 'http://localhost/Projet_TER/API/Controllers/utilisateur/supprimerUtilisateur.php',
                data: data
            })
                .then(response => {
                    console.log(response)
                    if (response.status === 200) {
                        this.setState({
                            users: response.data
                        })
                    }
                })
                .catch(function (erreur) {
                    console.log(erreur);
                });
        }
    }

    delete = (id) => {
        if (id !== null) {
            let data = JSON.stringify({id: Number(id)});
            console.log(data);
            axios({
                method: 'delete',
                url: 'http://localhost/Projet_TER/API/Controllers/utilisateur/supprimerUtilisateur.php',
                data: data
            })
                .then(response => {
                    console.log(response)
                    if (response.status === 200) {
                        this.setState({
                            users: response.data
                        })
                    }
                })
                .catch(function (erreur) {
                    console.log(erreur);
                });
        }
    }

    chargeData() {
        let listItems = [];
        for (var i in this.state.users) {
            let child = [];
            child.push(<div key={i} className="row">
                <p>{this.state.users[i]['Pseudo']}</p>
                <p>{this.state.users[i]['Mail']}</p>
                <p>{this.state.users[i]['Role']}</p>

                <p>
                    <FontAwesomeIcon icon={faPen} onClick={this.handleClickOpen.bind(this, this.state.users[i]['ID_Utilisateur'], this.state.users[i]['Role'])}/>
                    <Dialog open={this.state.modalOpen} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
                        <DialogContent>
                            <DialogContentText>
                                {this.state.idUser}
                            </DialogContentText>
                            <TextField
                                autoFocus
                                margin="dense"
                                id="name"
                                label="Email Address"
                                type="email"
                                fullWidth
                            />

                        </DialogContent>
                        <DialogActions>
                            <Button onClick={this.handleClose} color="primary">
                                Cancel
                            </Button>
                            <Button onClick={this.handleClose} color="primary">
                                Subscribe
                            </Button>
                        </DialogActions>
                    </Dialog>

                    <FontAwesomeIcon icon={faTrashAlt}
                                     onClick={this.delete.bind(this, this.state.users[i]['ID_Utilisateur'])}/>
                </p>
            </div>)
            listItems.push(child);
        }
        return listItems;
    }

    render() {
        if (this.state.users === null) return (<p>Loading...</p>);
        else {
            console.log(this.state.users);
            return (
                <div className="Users">
                    <div className="header">
                        <p><FontAwesomeIcon icon={faUser}/>Username</p>
                        <p><FontAwesomeIcon icon={faEnvelope}/>Mail</p>
                        <p><FontAwesomeIcon icon={faUserTag}/>Role </p>
                    </div>
                    {this.chargeData()}
                </div>
            );
        }
    }
}

export default Users;
