import React, {useState} from 'react';
import axios from 'axios';
import 'font-awesome/css/font-awesome.min.css';

const Register = ({history}) => {
    const [username, setUsername] = useState("");
    const [mail, setMail] = useState("");
    const [mailErrorMessage, setMailErrorMessage] = useState(null);
    const [pseudoErrorMessage, setPseudoErrorMessage] = useState(null);
    const [password, setPassword] = useState("");
    const [passwordConfirmation, setPasswordConfirmation] = useState("");



    const register= ()=> {
        let protocol = window.location.protocol;
        let host = window.location.hostname;
        let url = protocol + '//' + host;

        if (username !== "" && mail !== "" && password !== "" && passwordConfirmation !== "" && password === passwordConfirmation) {

            axios.post(url + '/API/Controllers/utilisateur/creerUtilisateur.php', {mail: mail, pseudo: username, mot_de_passe: password})
                .then(response => {
                    console.log(response.data);
                    if (response.data['ErrorMail']) {
                        setMailErrorMessage(response.data['ErrorMail']);
                    }
                    else setMailErrorMessage(null);
                    if (response.data['ErrorPseudo']) {
                        setPseudoErrorMessage(response.data['ErrorPseudo']);
                    }else setPseudoErrorMessage(null);
                    window.location.href = '/login'
                    history.replace('/login');
                })
                .catch(error => console.log(error))
        }
    }

    return (
        <div className="LoginRegister">
            <div>
                <h3>Sign up</h3>
                <div className="InputItems">
                    <div>
                        {pseudoErrorMessage ? <>
                                <input className={"ErrorInput"} id="username" name="username" required
                                       placeholder="ï€‡     Username"
                                       onChange={e => setUsername(e.target.value)}/>
                                <div className={"ErrorMessage"}>{pseudoErrorMessage}</div>
                            </>
                            : <>
                                <input type="text" id="username" name="username" required placeholder="ï€‡     Username"
                                       onChange={e => setUsername(e.target.value)}/>
                            </>
                        }
                    </div>
                    <div>
                        {mailErrorMessage ? <>
                                <input className={"ErrorInput"} type="mail" id="mail" name="mail" required
                                       placeholder="ïƒ      Mail"
                                       onChange={e => setMail(e.target.value)}/>
                            <div className={"ErrorMessage"}>{mailErrorMessage}</div>
                        </> :
                            <>
                                <input type="mail" id="mail" name="mail" required placeholder="ïƒ      Mail"
                                       onChange={e => setMail(e.target.value)}/>
                            </>}
                    </div>
                    <div>
                        <input type="password" id="password" name="password" required placeholder="ï€£     Password"
                               onChange={e => setPassword(e.target.value)}/>
                    </div>

                    <div>
                        <input type="password" id="passwordConfirmation" name="passwordConfirmation" required
                               placeholder={"ï€£     Confirm password"}
                               onChange={e => setPasswordConfirmation(e.target.value)}/>
                    </div>
                </div>

                <div className="action">
                    <button className="button filled" onClick={register}>Sign up</button>
                </div>
            </div>
        </div>
    );
}
export default Register;
