import React, {useEffect, useState} from 'react';
import axios from 'axios';

const Login =({history}) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [user, setUser] = useState({
        username: "",
        password: ""
    });

    const handleChange = ({currentTarget}) => {
      const  {name, value} = currentTarget;

      setUser({...user, [name]:value})
      console.log(name, value);
    }

    const handleSubmit = event =>{
        event.preventDefault();

        console.group();
        console.log(user);
        console.groupEnd();
    }

    useEffect(() => {
        history.replace('')
    }, [username]);


    function sendConnection() {
        let protocol = window.location.protocol;
        let host = window.location.hostname;
        let url = protocol + '//' + host;

        const json = JSON.stringify({username: username, mot_de_passe: password});
        axios.post(url + '/Projet_TER/API/Controllers/connexion.php', json)
            // axios.post(url + '/reactTest/MATUI/API/Controllers/connexion.php', json)
            .then(response => {
                console.log(response.data);
            })
            .catch(error => console.log(error))
    }


    return (
        // <div className="Login">
        //     <form>
        //         <div>
        //             <input type="text" id="username" name="username" required placeholder="Username"
        //             value={username} onChange={e => setUsername(e.target.value)}/>
        //         </div>
        //         <div>
        //             <input type="password" id="password" name="password" required placeholder="Password"
        //             value={password} onChange={e => setPassword(e.target.value)}/>
        //         </div>
        //     </form>
        //     <div className="action">
        //         <button onClick={sendConnection}>connect</button>
        //     </div>
        //     <div className="signup">
        //         <p>Don't have an account ? <a href="/register">Create one now</a></p>
        //     </div>
        // </div>
        <div className="LoginRegister">
            <div>
                <h3>Sign in</h3>
                <div className="InputItems">
                    <div>
                        {/*<input type="text" id="username" name="username" required placeholder="ï€‡     Username"*/}
                        {/*       onChange={e => setUsername(e.target.value)}/>*/}
                        <input type="text" id="username" name="username" required placeholder="ï€‡     Username"
                               onChange={handleChange}/>
                    </div>

                    <div>
                        <input type="password" id="password" name="password" required placeholder={"ï€£     Password"}
                               onChange={handleChange}/>
                    </div>
                </div>

                <div className="action">
                    <button className="button filled" onClick={handleSubmit}>Sign in</button>
                </div>

                <div className="signup">
                    <p>Don't have an account ? <a href="/register">Create one now</a></p>
                </div>
            </div>
        </div>
    );
}

export default Login;
