import React, {useState} from 'react';
import axios from 'axios';
import Issues from "../components/quiz/issues";
import Historic from "../components/quiz/historic";

export class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users : [],
            mail: [],
            pseudo:null,
            role:null

        };
        // this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidMount(){
            axios.get('http://localhost/Projet_TER/API/Controllers/utilisateur/lireUtilisateur.php')
                .then(response => {
                    this.setState({users: response.data});
                    for(let i =0; i<this.state.users['utilisateur'].length;i++){
                        let newStateArray = this.state.mail.slice();
                        newStateArray.push(this.state.users['utilisateur'][0]['Mail']);
                        this.setState({mail : newStateArray });
                    }
                    console.log(this.state.mail);
                })
                .catch(error => console.log(error))
    };


    render(){
        return (
            <div className="Users">
              <p>{this.state.mail}</p>
            </div>
        );
    }
}

export default Users;
