import React, {useEffect} from "react";
import $ from 'jquery';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faExclamationTriangle} from '@fortawesome/free-solid-svg-icons';

function ModalError(props) {

    function closeModal(){
        $('.ModalError').css("display", "none");
        props.close();
    }

    function openModal(){
        $('.ModalError').css("display", "block");
    }


    useEffect(() => {
        if(props.open){
            openModal();
        }
    }, [props.open]);


    return (
        <div className="Modal ModalError">
            <div>
                <FontAwesomeIcon className="icon" icon={faExclamationTriangle} /><h3>Error !</h3>
                {props.message &&
                <p>{props.message}</p>
                }
                <div className="action">
                    <button className="button filled" onClick={closeModal}>Cancel</button>
                </div>
            </div>
        </div>
    );
}

export default ModalError;
